  if (!user) return; 

  const userRef = firestore.doc(`user/${user.uid}`);
  const snapshot = await userRef.get();

  if(!snapshot.exists) {
    const {email} = user;
    const {username} = additionalData;

    try{
      userRef.set({
        username,
